INSERT INTO `web_video` VALUES (2,'Học gì để không thất nghiệp',NULL,NULL,'<p>Một m&oacute;n qu&agrave; d&agrave;nh tặng cho c&aacute;c bạn học sinh lớp 12 chuẩn bị thi đại học. H&atilde;y d&agrave;nh ra 5 ph&uacute;t để thay đổi cuộc đời bạn từ đ&acirc;y.</p>\r\n\r\n<p>Về ph&iacute;a Trường Cao đẳng Sư phạm Trung ương: Trưởng đo&agrave;n l&agrave; NGƯT. TS Đặng Lộc Thọ - B&iacute; thư Đảng uỷ, Hiệu trưởng Nh&agrave; trường, đi c&ugrave;ng đo&agrave;n c&oacute; l&atilde;nh đạo c&aacute;c khoa X&atilde; hội Nh&acirc;n văn, &Acirc;m Nhạc v&agrave; Mĩ thuật v&agrave; 25 đo&agrave;n vi&ecirc;n l&agrave; c&aacute;n bộ v&agrave; sinh vi&ecirc;n Nh&agrave; trường; Về ph&iacute;a Huyện uỷ, Huyện đo&agrave;n Ho&agrave;ng Su Ph&igrave; c&oacute; Đ/c Ho&agrave;ng Ngọc Đ&iacute;ch - UVBTV Huyện ủy, Trưởng ban Tuy&ecirc;n gi&aacute;o Huyện ủy; Đ/c Triệu Tiến Quang - Huyện ủy vi&ecirc;n, B&iacute; thư Huyện Đo&agrave;n; Đ/c Ho&agrave;ng Xu&acirc;n H&ograve;a - Ph&oacute; B&iacute; thư Huyện đo&agrave;n&nbsp; v&agrave; đại diện c&aacute;c ph&ograve;ng, ban ng&agrave;nh của Huyện uỷ...; Về ph&iacute;a L&atilde;nh đạo Đảng ủy, HĐND, UBND, Đo&agrave;n Thanh ni&ecirc;n x&atilde; c&oacute; Đ/c Gi&agrave;ng Văn L&acirc;m - B&iacute; thư Đảng ủy x&atilde;; Đ/c Trịnh C&ocirc;ng Hiển - Chủ tịch UBND x&atilde;; Đ/c Ly Seo V&agrave;ng - UVBCH Đảng ủy, B&iacute; thư Đo&agrave;n x&atilde; v&agrave; c&aacute;c đồng ch&iacute; Ph&oacute; B&iacute; thư Đảng ủy, Ph&oacute; Chủ tịch UBND, HĐND, BCH Đo&agrave;n x&atilde; v&agrave; c&aacute;c ph&ograve;ng ban x&atilde; Th&egrave;n Chu Ph&igrave;n.</p>\r\n\r\n<p>Trong 3 ng&agrave;y hoạt động, đo&agrave;n đ&atilde; phối hợp thực hiện c&aacute;c nội dung: x&acirc;y dựng c&ocirc;ng tr&igrave;nh &quot;M&aacute;i che cho em&quot; trị gi&aacute; 30 triệu đồng; trang tr&iacute; trong v&agrave; ngo&agrave;i khu&ocirc;n vi&ecirc;n trường Mầm non x&atilde;; tặng qu&agrave; c&aacute;c gia đ&igrave;nh ch&iacute;nh s&aacute;ch v&agrave; học sinh ngh&egrave;o, trị gi&aacute; 3 triệu đồng, tặng qu&agrave; Đảng uỷ, UBND, HĐND v&agrave; CB Gi&aacute;o vi&ecirc;n trường mầm non x&atilde; trị gi&aacute; 5 triệu đồng. Tổ chức chương tr&igrave;nh giao lưu văn nghệ với Huyện đo&agrave;n, UBND, HĐND, Đo&agrave;n x&atilde;, trường MN...v&agrave; b&agrave; con nh&acirc;n d&acirc;n địa phương.</p>\r\n','https://www.youtube.com/watch?v=Hil4b8lNC7s',NULL,NULL,NULL,1,0,2,1471363334,NULL,1,1488943441,NULL,NULL,NULL),(3,'Đến giờ học thì làm gì?',NULL,NULL,'<p>Về ph&iacute;a Trường Cao đẳng Sư phạm Trung ương: Trưởng đo&agrave;n l&agrave; NGƯT. TS Đặng Lộc Thọ - B&iacute; thư Đảng uỷ, Hiệu trưởng Nh&agrave; trường, đi c&ugrave;ng đo&agrave;n c&oacute; l&atilde;nh đạo c&aacute;c khoa X&atilde; hội Nh&acirc;n văn, &Acirc;m Nhạc v&agrave; Mĩ thuật v&agrave; 25 đo&agrave;n vi&ecirc;n l&agrave; c&aacute;n bộ v&agrave; sinh vi&ecirc;n Nh&agrave; trường; Về ph&iacute;a Huyện uỷ, Huyện đo&agrave;n Ho&agrave;ng Su Ph&igrave; c&oacute; Đ/c Ho&agrave;ng Ngọc Đ&iacute;ch - UVBTV Huyện ủy, Trưởng ban Tuy&ecirc;n gi&aacute;o Huyện ủy; Đ/c Triệu Tiến Quang - Huyện ủy vi&ecirc;n, B&iacute; thư Huyện Đo&agrave;n; Đ/c Ho&agrave;ng Xu&acirc;n H&ograve;a - Ph&oacute; B&iacute; thư Huyện đo&agrave;n&nbsp; v&agrave; đại diện c&aacute;c ph&ograve;ng, ban ng&agrave;nh của Huyện uỷ...; Về ph&iacute;a L&atilde;nh đạo Đảng ủy, HĐND, UBND, Đo&agrave;n Thanh ni&ecirc;n x&atilde; c&oacute; Đ/c Gi&agrave;ng Văn L&acirc;m - B&iacute; thư Đảng ủy x&atilde;; Đ/c Trịnh C&ocirc;ng Hiển - Chủ tịch UBND x&atilde;; Đ/c Ly Seo V&agrave;ng - UVBCH Đảng ủy, B&iacute; thư Đo&agrave;n x&atilde; v&agrave; c&aacute;c đồng ch&iacute; Ph&oacute; B&iacute; thư Đảng ủy, Ph&oacute; Chủ tịch UBND, HĐND, BCH Đo&agrave;n x&atilde; v&agrave; c&aacute;c ph&ograve;ng ban x&atilde; Th&egrave;n Chu Ph&igrave;n.</p>\r\n\r\n<p>Trong 3 ng&agrave;y hoạt động, đo&agrave;n đ&atilde; phối hợp thực hiện c&aacute;c nội dung: x&acirc;y dựng c&ocirc;ng tr&igrave;nh &quot;M&aacute;i che cho em&quot; trị gi&aacute; 30 triệu đồng; trang tr&iacute; trong v&agrave; ngo&agrave;i khu&ocirc;n vi&ecirc;n trường Mầm non x&atilde;; tặng qu&agrave; c&aacute;c gia đ&igrave;nh ch&iacute;nh s&aacute;ch v&agrave; học sinh ngh&egrave;o, trị gi&aacute; 3 triệu đồng, tặng qu&agrave; Đảng uỷ, UBND, HĐND v&agrave; CB Gi&aacute;o vi&ecirc;n trường mầm non x&atilde; trị gi&aacute; 5 triệu đồng. Tổ chức chương tr&igrave;nh giao lưu văn nghệ với Huyện đo&agrave;n, UBND, HĐND, Đo&agrave;n x&atilde;, trường MN...v&agrave; b&agrave; con nh&acirc;n d&acirc;n địa phương.</p>\r\n','https://www.youtube.com/watch?v=9QabhxE1N7U',NULL,NULL,NULL,1,0,1,1488853954,NULL,1,1488943455,NULL,NULL,NULL);
